	var firstSlide = "<div class='slide' id='cloneFirst'>" + $("#slide1").html() + "</div>";
	$(".slider-frame").append(firstSlide);
	
	var lastSlide = "<div class='slide' id='cloneLast'>" + $("#slide" + numSlides).html() + "</div>";
	$(".slider-frame").prepend(lastSlide);
	
	///// Start Nav
	
